INSERT INTO restaurants (
    name, address, description, image, price_tier, p_t_description, concept, c_description, diet, d_description
) VALUES
  ('Non Solo', 'Av. du Tribunal-Fédéral 5',
   'L''Italie comme si vous y étiez ! Les plats maison à l''emporter ou sur place, les produits d''épicerie et de salumeria sélectionnés, traiteur pour vos évènements, le vrai bon café, toute la culture de l''Italie réunie à Lausanne ... e non solo !',
   '/assets/images/non_solo.jpg', '$', 'Budget-friendly', 'Italian', 'Cuisine traditionnelle italienne avec des produits importés et faits maison.', 'Vegetarian', 'Propose des pizzas et des plats de pâtes végétariens, avec quelques options véganes disponibles.'),

  ('Holy Cow', 'Rue Cheneau-de-Bourg 17',
   'Fast, fresh and local. From its humble beginnings in Lausanne and after a decade of national growth and development, Holy Cow! has remained true to its philosophy: Swissness, swiss taste, and swiss made are an integral part of Holy Cow! DNA.',
   '/assets/images/holy_cow.jpg', '$$', 'Affordable yet premium', 'Hamburger', 'Burgers gourmets avec un accent sur la qualité locale et la rapidité.', 'Flexitarian', 'Inclut des burgers à base de viande, mais aussi des alternatives végétariennes et végétaliennes.'),

  ('Classic', 'Av. Georgette 2',
   'Des ingrédients ultra-frais, des recettes audacieuses et une explosion de saveurs à chaque bouchée. Burgers gourmets, sandwiches savoureux, salades rafraîchissantes : chez Classic, chaque envie trouve son bonheur. Halal-friendly. Commandez maintenant et laissez-vous tenter !',
   '/assets/images/classic.png', '$$', 'Modéré avec options gourmandes', 'Hamburger', 'Fusion de street food moderne et de burgers artisanaux.', 'Vegetarian', 'Propose des alternatives végétariennes savoureuses, parfait pour une alimentation sans viande.'),

  ('Black Tap', 'Rue Enning 10',
   'Black Tap is keeping that timeless NYC vibe alive, all over the world. Everything you see, taste, hear and feel - is fresh, fun and familiar. Full of the street art we love. The music we live for. The fun-to-be-with people who bring it, every day.',
   '/assets/images/black_tap.jpg', '$$$', 'Premium and trendy', 'Hamburger', 'Expérience gastronomique de type New York Street avec des burgers signature.', 'Flexitarian', 'Offre des plats riches en saveurs avec options végétariennes et viandes de qualité.'),

  ('Le Nil Bleu', 'Av. Villamont 17',
   'Le Nil-Bleu est un petit restaurant familial de spécialités éthiopiennes situé au cœur de Lausanne dans le quartier Georgette',
   '/assets/images/nil_bleu.jpg', '$$', 'Cuisine moyenne gamme', 'Éthiopien', 'Spécialités africaines avec service familial et ambiance chaleureuse.', 'Vegetarian', 'Beaucoup de plats traditionnels éthiopiens sont naturellement végétariens ou véganes.'),

  ('Doki Doki', 'Av. du Tribunal-Fédéral 4',
   'Doki Doki est un ramen shop basé à Lausanne. Favorisant produits locaux et préparations faites maison, notre objectif est de progresser dans notre quête du bol de ramen ultime.',
   '/assets/images/doki_doki.jpg', '$$$', 'Prix élevé mais qualité supérieure', 'Japanese', 'Ramen maison inspirés de la tradition japonaise avec une touche locale.', 'Gluten-Free', 'Des options de ramen sans gluten sont disponibles, bien que limitées.'),

  ('Le Kotchi Cité', 'Rue Louis-Auguste Curtat 6',
   'Small Japanese Street Food Style',
   '/assets/images/kotchi.png', '$', 'Petit budget', 'Japanese', 'Snacks japonais rapides et authentiques en format street food.', 'Vegetarian', 'Propose des onigiris et plats sans viande, adaptés aux végétariens.'),

  ('Chez Mario', 'Rue de Bourg 28',
   'Chez Mario a été fondée en 1958 au coeur de Lausanne, dans la rue de Bourg. La pizzeria italienne se base sur les traditions et l’authenticité pour vous délivrer des produits de qualités.',
   '/assets/images/chez_mario.jpg', '$', 'Abordable', 'Italian', 'Pizzas et pâtes traditionnelles italiennes avec une longue histoire.', 'Flexitarian', 'Plusieurs options végétariennes disponibles, tout en conservant des plats classiques à base de viande.'),

  ('The Greek Project', 'Rue Marterey 1/3',
   'An emphasis on togetherness, the importance of family and friends coming together to share meals and bond over food and stories. A leisurely place in a rigorous everyday life that makes time for connection and laughter.',
   '/assets/images/greek_project.jpeg', '$$', 'Prix moyen avec ambiance chaleureuse', 'Greek', 'Cuisine grecque moderne axée sur le partage et la convivialité.', 'Vegetarian', 'De nombreuses recettes grecques végétariennes comme les dolmas ou les salades complètes.'),

  ('Poke Me', 'Rue Caroline 7',
   'À mi-chemin entre le maki, le chirachi et la salade, le poke bowl, est le nouveau phénomène healthy du moment. Et c’est loin d’être nouveau ! Il s’agit simplement de la recette traditionnelle Hawaïenne qui s’est installée depuis peu en Europe !',
   '/assets/images/poke_me.webp', '$', 'Repas rapide et abordable', 'Veggie', 'Concept centré sur des bowls équilibrés à base de légumes, tofu ou poisson.', 'Vegan', 'Spécialisé dans les bowls végétariens et véganes avec ingrédients frais et sains.')
